var popup=document.querySelector(".popup"),link=document.querySelector(".btn-contacts"),close=popup.querySelector(".btn-close"),ENTER_KEY_CODE=27;link.addEventListener("click",function(a){a.preventDefault();popup.classList.add("popup-show")});close.addEventListener("click",function(a){a.preventDefault();popup.classList.remove("popup-show")});window.addEventListener("keydown",function(a){a.keyCode===ENTER_KEY_CODE&&popup.classList.contains("popup-show")&&popup.classList.remove("popup-show")});
